mapper.select:

SELECT tsu.*
    , ROW_NUMBER() OVER(
        ORDER BY tsu.ACCES_URL
    ) AS RN
    , COUNT(*) OVER() AS TOT_CNT
FROM t_sys_url tsu


        AND (tsu.acces_url LIKE CONCAT('%',#{schWord},'%')
            OR tsu.url_desc LIKE CONCAT('%',#{schWord},'%'))


        AND tsu.alw_div = #{alwDiv}


        AND tsu.use_yn = #{useYn}

mapper.insert:
INSERT INTO T_SYS_URL(
      acces_url
    , url_desc
    , alw_div
    , use_yn
    , reg_login_id
    , reg_dte
    , upt_login_id
    , upt_dte
)VALUES(
    #{accesUrl}
    , #{urlDesc}
    , #{alwDiv}
    , #{useYn}
    , #{loginInfo.username}
    , CURRENT_TIMESTAMP
    , #{loginInfo.username}
    , CURRENT_TIMESTAMP
)

mapper.update:
UPDATE T_SYS_URL
SET upt_dte = CURRENT_TIMESTAMP
    , upt_login_id = #{loginInfo.username}

    , url_desc = #{urlDesc}


    , alw_div = #{alwDiv}


    , use_yn = #{useYn}

WHERE acces_url = #{accesUrl}
